<?phpfunction getmicrotime() {     	list($usec, $sec) = explode(" ",microtime());     	return ((float)$usec + (float)$sec); }function sortArray($array, $key_s) { 	$sort_values=array();   for ($i = sizeof($array)-1; $i >=0; $i--) {        $sort_values[$i] = $array[$i][$key_s];    }      asort ($sort_values);    reset ($sort_values);    $sorted_arr=array();    while (list ($arr_key, $arr_val) = each ($sort_values)) {          $sorted_arr[] = $array[$arr_key];    }    return $sorted_arr;   } function highlight_matches($find_text, $text) {  return preg_replace("/($find_text)/i", '<span class="searchHilight">$1</span>', $text);}function makeLink_matches($find_text, $text,$url,$title) {  return preg_replace("/($find_text)/i", '<a class="highlightTagLink" href="'.$url.'" '.$title.'>$1</a>', $text);}function makeDescription_matches($find_text, $text,$title) {  return preg_replace("/($find_text)/i", '<span '.$title.'>$1</span>', $text);}?>